import React from 'react';
import styled from 'styled-components';
import HeroImg from '../../img/Hero2.jpg'

const HeroSection = styled.div`
position: relative;
background-image: url(${HeroImg});
width: 100%;
height: 600px;
background-position: center;
background-size: cover;
z-index: -1;
`;
const HeroOverFlow = styled.div`
background-color: #fff;
opacity: 0.7;
width: 100%;
height: 100%;
`;

const HeroContainer = styled.div`
width: 88%;
margin: 0 auto;
height: 100vh;
display: flex;
flex-direction: column;
align-items: left;
justify-content: center;
z-index: 101;
`;
const HeroContent = styled.div`
width: 60%;
line-height: 1.5;
`;
const Subtitle = styled.h3`
color: #F56362;
font-family: 'Balsamiq Sans', cursive;
font-size: 25px;
letter-spacing: 1px;
`;
const Title = styled.h1`
font-size: 50px;
letter-spacing: 1px;
text-transform: uppercase;
color: #F56362;
opacity: 1;
z-index: 1;
`;
const Desc = styled.p`
font-size: 15px;

`;


const Hero = () => {
    return (
        <HeroSection>
            <HeroOverFlow>
                <HeroContainer>
                <HeroContent>
                    <Subtitle>Saubere und professionelle,</Subtitle>
                    <Title>Mehrzweck-Landingpage</Title>
                    <Desc>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed feugiat arcu ut orci porta, eget porttitor felis suscipit. Sed a nisl ullamcorper, tempus augue at, rutrum lacus. Duis et turpis eros.
                    </Desc>
                </HeroContent>
                </HeroContainer>
            </HeroOverFlow>
        </HeroSection>
    )
}

export default Hero
