import React from 'react';
import styled from 'styled-components';


const InfoSection = styled.div`
position: relative;
padding: 100px 0;
width: 100%;
height: auto;
overflow: hidden;

`;
const InfoContainer = styled.div`
width: 88%;
margin: 0 auto;
`;
const Heading = styled.h2`
margin-top: 60px;
text-align: center;
font-size: 40px;
font-weight: 700;
`;
const RowOne = styled.div`
    display: flex;

`;
const InfoTitle = styled.h4``;
const ColLeft = styled.div`
    display: flex;
    width: 100%;
    height: auto;
    flex-direction: column;


`;
const InfoDescOne = styled.p`
font-size: 15px;
font-weight: 300;
`;
const ColRight = styled.div``;

const Features = ({heading, pragraphOne, pragraphTwo, image}) => {
    return (
        <InfoSection>
            <InfoContainer>
                <Heading>Fantastisches neues Feature</Heading>
                <RowOne>
                    <ColLeft>
                        <InfoTitle>{ heading }</InfoTitle>
                        <InfoDescOne>{ pragraphOne }</InfoDescOne>
                        <InfoDescOne>{ pragraphTwo }</InfoDescOne>
                    </ColLeft>
                    <ColRight>
                    <img src={image} alt=""/>
                    </ColRight>
                </RowOne>
            </InfoContainer>
        </InfoSection>
    )
}

export default Features
