import React from 'react';
import styled from 'styled-components';
import HeroImg from '../../img/hero3.jpg'

const HeroSection = styled.div`
position: relative;
top: 80px;
background-image: url(${HeroImg});
width: 100%;
height: 700px;
background-position: center;
background-size: cover;
z-index: -2;
text-align: right;
overflow: hidden
`;

const HeroContainer = styled.div`
width: 88%;
margin: 0 auto;
height: 100vh;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
z-index: 101;
`;
const HeroContent = styled.div`

line-height: 1.5;
`;
const Subtitle = styled.h3`
color: #fff;
font-family: 'Balsamiq Sans', cursive;
font-size: 25px;
letter-spacing: 1px;
`;
const Title = styled.h1`
font-size: 50px;
letter-spacing: 1px;
text-transform: uppercase;
color: #fff;
opacity: 1;
z-index: 1;
text-shadow: 1px 1px 2px black;
`;
const Desc = styled.p`
font-size: 15px;
color: #fff;
`;


const Hero = () => {
    return (
        <HeroSection>
                <HeroContainer>
                <HeroContent>
                    <Subtitle>Saubere und professionelle,</Subtitle>
                    <Title>Mehrzweck-Landingpage</Title>
                    <Desc>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed feugiat arcu ut orci porta, eget porttitor felis suscipit. Sed a nisl ullamcorper, tempus augue at, rutrum lacus. Duis et turpis eros.
                    </Desc>
                </HeroContent>
                </HeroContainer>
        </HeroSection>
    )
}

export default Hero
