import React from 'react';
import { Button } from '../Button';
import styled from 'styled-components';


const InfoSection = styled.div`
position: relative;
padding: 100px 0;
width: 100%;
height: auto;
overflow: hidden;

`;
const InfoContainer = styled.div`
width: 88%;
margin: 0 auto;
`;
const Heading = styled.h2`
margin: 60px 0 100px;
text-align: center;
font-size: 40px;
font-weight: 700;
@media screen and (max-width: 768px){
    margin-bottom: 50px;
}
`;
const RowOne = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 30px;

    @media screen and (max-width: 768px){
        grid-template-columns: 1fr;
    }

`;
const ColLeft = styled.div`
    width: 100%;
    line-height: 1.7;
    display: flex;
    align-items: center;
    /* justify-content: center; */
    flex-direction: column;

`;
const InfoTitle = styled.h3``;
const InfoDescOne = styled.p`
font-size: 15px;
font-weight: 300;
`;
const ColRight = styled.div`

    img{
        width: 100%;
        height: auto;
        object-fit: cover;
    }
`;
const BtnWrapper = styled.div`
display: inline-block;
margin-top: 30px;
`;

const Features = ({heading, pragraphOne, pragraphTwo, image}) => {
    return (
        <InfoSection>
            <InfoContainer>
                <Heading>Fantastisches neues Feature</Heading>
                <RowOne>
                    <ColLeft>
                        <InfoTitle>{ heading }</InfoTitle>
                        <InfoDescOne>{ pragraphOne }</InfoDescOne>
                        <InfoDescOne>{ pragraphTwo }</InfoDescOne>
                        <BtnWrapper>
                        <Button 
                        isred='true'
                        iswhite='true'

                        >Mehr erfahren</Button>
                        </BtnWrapper>
                    </ColLeft>
                    <ColRight>
                        <img src={image} alt=""/>
                    </ColRight>
                </RowOne>
            </InfoContainer>
        </InfoSection>
    )
}

export default Features
